-- title:  Conway's Game of Life
-- author: Jeremy Miller
-- desc:   Conway's Game of Life
-- script: lua

--[[
	Rules of Life:
	1. Any live cell with fewer than two live neighbours dies, as if by underpopulation.
	2. Any live cell with two or three live neighbours lives on to the next generation.
	3. Any live cell with more than three live neighbours dies, as if by overpopulation.
	4. Any dead cell with exactly three live neighbours becomes a live cell, as if by reproduction.
--]]

-- BUTTONS
A=4

WIDTH=240
HEIGHT=136

LIVE=12 -- color of living cell
MIN_LIVING=100
MAX_LIVING=WIDTH*HEIGHT

function init()
	board={}
	createBoard()
	populateBoard()
end

function createBoard()
	for y=0,HEIGHT-1 do
		board[y]={}
		for x=0,WIDTH-1 do
			board[y][x]=false -- all cells start as "dead"
		end
	end
end

function populateBoard()
	local numLiving=math.random(MIN_LIVING/2,(MAX_LIVING/2))
	for i=0,numLiving do
		local x=math.random(WIDTH-1)
		local y=math.random(HEIGHT-1)
		board[y][x]=true
	end
end

function input()
	if btnp(A) then
		-- reset game
		init()
	end
end

function update()
	local newBoard={}
	for y=0,#board do
		newBoard[y]={}
		for x=0,#board[y] do
			newBoard[y][x]=false -- assume new cell is "dead"
			local numLivingNeighbors=getLivingNeighbors(x,y)
			if board[y][x] then -- current cell is "alive"
				if numLivingNeighbors<2 then
					-- rule 1
					newBoard[y][x]=false
				elseif numLivingNeighbors==2 or
											numLivingNeighbors==3 then
					-- rule 2
					newBoard[y][x]=true
				elseif numLivingNeighbors>3 then
					-- rule 3
					newBoard[y][x]=false
				end
			else -- current cell is "dead"
				if numLivingNeighbors==3 then
					-- rule 4
					newBoard[y][x]=true
				end
			end
		end
	end
	board=newBoard
end

function getLivingNeighbors(x,y)
	local livingNeighbors=0
	for y2=-1,1 do
		for x2=-1,1 do
			local neighborX=x+x2
			local neighborY=y+y2
			-- make sure we're not off the board
			if neighborX>=0 and neighborY>=0 and
						neighborX<WIDTH and neighborY<HEIGHT and
						not (x2==0 and y2==0) and -- don't count current cell
						board[neighborY][neighborX] then
				livingNeighbors=livingNeighbors+1
			end
		end
	end	
	return livingNeighbors
end

function draw()
	for y=0,#board-1 do
		for x=0,#board[y]-1 do
			if board[y][x] then
				pix(x,y,LIVE)
			end
		end
	end
end

init()

function TIC()
	cls(0)
	input()
	update()
	draw()
end

